import logo from './logo.svg';
import './App.css';
import AddTicket from './pages/AddTicket';
import LoggedTickets from './pages/LoggedTickets';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'

import { ListGroup } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { BrowserRouter, Routes } from 'react-router-dom';



function App() {

  return (
  <div>
      <BrowserRouter>
      <Routes>
          <Route path="/" element={<Home/>}></Route>
          <Route path="LoggedTickets" element={<LoggedTickets/>}></Route>
          <Route path="AddTicket" element={<AddTicket/>}></Route>
      </Routes>
      </BrowserRouter>

      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />   
        <span>IT Service Management System</span>
      </header>  


      <Container fluid>      
      <Row>
        <Col xxl={3} ><Menu /></Col>
        <Col xxl={8}><LoggedTickets /></Col>
      </Row>
     {/*  <Row>
          <Col xxl={8} ><AddTicket/></Col>         
      </Row>  */}       
      </Container>
   </div>

  );
}

function Menu(){
  return (<div className="Menu">
    <ListGroup defaultActiveKey="#link1">
      <ListGroup.Item action href="#link1">
        Logged Tickets
      </ListGroup.Item>
      <ListGroup.Item action href="#link2">
        Create New Tickets
      </ListGroup.Item>
      <ListGroup.Item  action href="#link3">
        Statistics
      </ListGroup.Item>
    </ListGroup>
  </div>)
}



export default App;
