import logo from './logo.svg';
import './App.css';
import AddTicket from './pages/AddTicket';
import LoggedTickets from './pages/LoggedTickets';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav  from 'react-bootstrap/Nav';
import NavItem  from 'react-bootstrap/NavItem';

import { ListGroup } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { BrowserRouter, Routes,Route,Link } from 'react-router-dom';



function App() {

  return (
  <div>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />   
        <span>IT Service Management System</span>
      </header>  
      <Container fluid>      
      <Row>
        <Col xxl={3} ><Menu /></Col>
        <Col xxl={8} > 
         <BrowserRouter>
          <Routes>          
          <Route path="/" element={<LoggedTickets/>}></Route>
          <Route path="addTicket" element={<AddTicket/>}></Route>
          </Routes>
      </BrowserRouter></Col>
      </Row>
      </Container>    

   </div>

  );
}

function Menu(){
  return (<div className="Menu">
    <ListGroup defaultActiveKey="#/">
      <ListGroup.Item action href="#/">
        Logged Tickets
      </ListGroup.Item>
      <ListGroup.Item action href="addTicket">
        Create New Tickets
      </ListGroup.Item>
      <ListGroup.Item  action href="#AddTicket">
        Statistics
      </ListGroup.Item>
    </ListGroup>


    <Nav defaultActiveKey="/" className="flex-column">
      <Nav.Link href="/">Logged Tickets</Nav.Link>
      <Nav.Link eventKey="addTicket">Create Ticket</Nav.Link>
      <Nav.Link eventKey="addTicket">Statistics</Nav.Link>    
    </Nav>

    <Nav className="ml-auto">
    <NavItem> <Link className="nav-link"   to="/">Home</Link> </NavItem> 
    <NavItem> <Link  className="nav-link" to="/about">About</Link> </NavItem> 
    <NavItem> <Link className="nav-link"   to="/contact">Contact</Link> </NavItem> 
    </Nav>
  </div>)
}



export default App;
